add_lim_labels <- function(df){
  df <- df %>%
    mutate(label_int = paste(PEVage, PEVrounds, EPIbooster, EPIextra, massbooster_rep),
           labels = case_when(label_int == "- - 12m - -"  ~ 'Routine age-based',
                              label_int == "- - 12m 10y -" ~ "10y booster",
                              label_int == '- - 12m 5y -' ~ "5y booster",
                              label_int == '- - 12m 2y -' ~ "2y booster",
                              label_int == '- - 12m 5y+10y -' ~ "5y, 10y boosters",
                              label_int == '- - 12m 2y+10y -' ~ "2y, 10y boosters",
                              label_int == '- - 12m 2y+5y -' ~ "2y, 5y boosters",
                              label_int == '- - 12m 2y+5y+10y -' ~ "2y, 5y, 10y boosters",
                              label_int == '- - seasonal - -' ~ 'Seasonal vaccination',
                              label_int == 'hybrid - - seasonal - -' ~ 'Hybrid vaccination',
                              label_int == '5-14 - 12m - -' | label_int == 'catch-up 5-14 - 12m - -' ~ '5-14y',
                              label_int == '5-14 - 12m 10y -' | label_int == 'catch-up 5-14 - 12m 10y -' ~ '5-14y;\n10y booster',
                              label_int == '5-14 - 12m 5y -' | label_int == 'catch-up 5-14 - 12m 5y -' ~ '5-14y;\n5y booster',
                              label_int == '5-14 - 12m 2y -' | label_int == 'catch-up 5-14 - 12m 2y -' ~ '5-14y;\n2y booster',
                              label_int == '5-14 - 12m 5y+10y -' | label_int == 'catch-up 5-14 - 12m 5y+10y -' ~ '5-14y;\n5y, 10y boosters',
                              label_int == '5-14 - 12m 2y+10y -' | label_int == 'catch-up 5-14 - 12m 2y+10y -' ~ '5-14y;\n2y, 10y boosters',
                              label_int == '5-14 - 12m 2y+5y -' | label_int == 'catch-up 5-14 - 12m 2y+5y -' ~ '5-14y;\n2y, 5y boosters',
                              label_int == '5-14 - 12m 2y+5y+10y -' | label_int == 'catch-up 5-14 - 12m 2y+5y+10y -' ~ '5-14y;\n2y, 5y, 10y boosters',
                              label_int == '5-9 - 12m - -' | label_int == 'catch-up 5-9 - 12m - -' ~ '5-9y',
                              label_int == '5-9 - 12m 10y -' | label_int == 'catch-up 5-9 - 12m 10y -' ~ '5-9y;\n10y booster',
                              label_int == '5-9 - 12m 5y -' | label_int == 'catch-up 5-9 - 12m 5y -' ~ '5-9y;\n5y booster',
                              label_int == '5-9 - 12m 2y -' | label_int == 'catch-up 5-9 - 12m 2y -' ~ '5-9y;\n2y booster',
                              label_int == '5-9 - 12m 5y+10y -' | label_int == 'catch-up 5-9 - 12m 5y+10y -' ~ '5-9y;\n5y, 10y boosters',
                              label_int == '5-9 - 12m 2y+10y -' | label_int == 'catch-up 5-9 - 12m 2y+10y -' ~ '5-9y;\n2y, 10y boosters',
                              label_int == '5-9 - 12m 2y+5y -' | label_int == 'catch-up 5-9 - 12m 2y+5y -' ~ '5-9y;\n2y, 5y boosters',
                              label_int == '5-9 - 12m 2y+5y+10y -' | label_int == 'catch-up 5-9 - 12m 2y+5y+10y -' ~ '5-9y;\n2y, 5y, 10y boosters',
                              label_int == '6m-14y - 12m - -' ~ '6m-14y',
                              label_int == '6m-14y - 12m 10y -' ~ '6m-14y;\n10y booster',
                              label_int == '6m-14y - 12m 2y -' ~ '6m-14y;\n2y booster',
                              label_int == '6m-14y - 12m 5y -' ~ '6m-14y;\n5y booster',
                              label_int == '6m-14y - 12m 5y+10y -' ~ '6m-14y;\n5y, 10y boosters',
                              label_int == '6m-14y - 12m 2y+5y -' ~ '6m-14y;\n2y, 5y boosters',
                              label_int == '6m-14y - 12m 2y+10y -' ~ '6m-14y;\n2y, 10y boosters',
                              label_int == '6m-14y - 12m 2y+5y+10y -' ~ '6m-14y;\n2y, 5y, 10y boosters',
                              label_int == '6m-2y - 12m - -' ~ '6m-2y',
                              label_int == '6m-2y - 12m 10y -' ~ '6m-2y;\n10y booster',
                              label_int == '6m-2y - 12m 5y -' ~ '6m-2y;\n5y booster',
                              label_int == '6m-2y - 12m 2y -' ~ '6m-2y;\n2y booster',
                              label_int == '6m-2y - 12m 5y+10y -' ~ '6m-2y;\n5y, 10y boosters',
                              label_int == '6m-2y - 12m 2y+5y -' ~ '6m-2y;\n2y, 5y boosters',
                              label_int == '6m-2y - 12m 2y+10y -' ~ '6m-2y;\n2y, 10y boosters',
                              label_int == '6m-2y - 12m 2y+5y+10y -' ~ '6m-2y;\n2y, 5y, 10y boosters',
                              label_int == '6m-4y - 12m - -' ~ '6m-4y',
                              label_int == '6m-4y - 12m 10y -' ~ '6m-4y;\n10y booster',
                              label_int == '6m-4y - 12m 5y -' ~ '6m-4y;\n5y booster',
                              label_int == '6m-4y - 12m 2y -' ~ '6m-4y;\n2y booster',
                              label_int == '6m-4y - 12m 5y+10y -' ~ '6m-4y;\n5y, 10y boosters',
                              label_int == '6m-4y - 12m 2y+5y -' ~ '6m-4y;\n2y, 5y boosters',
                              label_int == '6m-4y - 12m 2y+10y -' ~ '6m-4y;\n2y, 10y boosters',
                              label_int == '6m-4y - 12m 2y+5y+10y -' ~ '6m-4y;\n2y, 5y, 10y boosters',
                              label_int == '6m-9y - 12m - -' ~ '6m-9y',
                              label_int == '6m-9y - 12m 10y -' ~ '6m-9y;\n10y booster',
                              label_int == '6m-9y - 12m 5y -' ~ '6m-9y;\n5y booster',
                              label_int == '6m-9y - 12m 2y -' ~ '6m-9y;\n2y booster',
                              label_int == '6m-9y - 12m 5y+10y -' ~ '6m-9y;\n5y, 10y boosters',
                              label_int == '6m-9y - 12m 2y+5y -' ~ '6m-9y;\n2y, 5y boosters',
                              label_int == '6m-9y - 12m 2y+10y -' ~ '6m-9y;\n2y, 10y boosters',
                              label_int == '6m-9y - 12m 2y+5y+10y -' ~ '6m-9y;\n2y, 5y, 10y boosters',
                              label_int == '- - - - -' ~ 'None'),
           labels = factor(labels, levels = c('Routine age-based', "2y booster","5y booster", "10y booster","5y, 10y boosters",
                                              '2y, 5y boosters','2y, 10y boosters', '2y, 5y, 10y boosters',
                                              'Seasonal vaccination', 'Hybrid vaccination',
                                              '6m-2y', '6m-2y;\n2y booster', '6m-2y;\n5y booster', '6m-2y;\n10y booster', '6m-2y;\n5y, 10y boosters','6m-2y;\n2y, 5y boosters','6m-2y;\n2y, 10y boosters','6m-2y;\n2y, 5y, 10y boosters',
                                              '6m-4y', '6m-4y;\n2y booster', '6m-4y;\n5y booster', '6m-4y;\n10y booster', '6m-4y;\n5y, 10y boosters','6m-4y;\n2y, 5y boosters','6m-4y;\n2y, 10y boosters','6m-4y;\n2y, 5y, 10y boosters',
                                              '6m-9y', '6m-9y;\n2y booster', '6m-9y;\n5y booster', '6m-9y;\n10y booster', '6m-9y;\n5y, 10y boosters','6m-9y;\n2y, 5y boosters','6m-9y;\n2y, 10y boosters','6m-9y;\n2y, 5y, 10y boosters',
                                              '6m-14y', '6m-14y;\n2y booster', '6m-14y;\n5y booster', '6m-14y;\n10y booster', '6m-14y;\n5y, 10y boosters','6m-14y;\n2y, 5y boosters','6m-14y;\n2y, 10y boosters','6m-14y;\n2y, 5y, 10y boosters',
                                              '5-9y', '5-9y;\n2y booster', '5-9y;\n5y booster', '5-9y;\n10y booster', '5-9y;\n5y, 10y boosters','5-9y;\n2y, 5y boosters','5-9y;\n2y, 10y boosters','5-9y;\n2y, 5y, 10y boosters',
                                              '5-14y', '5-14y;\n2y booster', '5-14y;\n5y booster', '5-14y;\n10y booster', '5-14y;\n5y, 10y boosters','5-14y;\n2y, 5y boosters','5-14y;\n2y, 10y boosters','5-14y;\n2y, 5y, 10y boosters',
                                              'None')))
  
  return(df)
}